CREATE TABLE user (
	id INTEGER NOT NULL, 
	username VARCHAR(20) NOT NULL, 
	email VARCHAR(120) NOT NULL, 
	image_file VARCHAR(20) NOT NULL, 
	password VARCHAR(60) NOT NULL, 
	PRIMARY KEY (id), 
	UNIQUE (username), 
	UNIQUE (email)
);
CREATE TABLE post (id INTEGER NOT NULL, title VARCHAR(100) NOT NULL, date_posted DATETIME NOT NULL, content TEXT NOT NULL, user_id INTEGER NOT NULL, main_image_filename text, main_image_caption VARCHAR(150), number_of_extra_images INTEGER DEFAULT 0, extra_images_filenames TEXT, link_href VARCHAR(150), link_description TEXT, PRIMARY KEY (id), FOREIGN KEY(user_id) REFERENCES user (id));
CREATE TABLE carousel_slider (id INTEGER PRIMARY KEY, image_filename VARCHAR(300), caption VARCHAR(500));
CREATE TABLE issue (issue_number INTEGER NOT NULL, year INTEGER, month TEXT, pdf_filename TEXT, thumbnail_filename TEXT);
CREATE TABLE text_and_image (id INTEGER PRIMARY KEY, text_part TEXT, image_filename VARCHAR(80));
CREATE TABLE IF NOT EXISTS nba_stat (id INTEGER PRIMARY KEY AUTO_INCREMENT, name VARCHAR(50), position varchar(8), age INTEGER, team varchar(3), games INTEGER, games_started INTEGER, minutes_played INTEGER, field_goals INTEGER, field_goal_attempts INTEGER, field_goal_percentage DECIMAL(4,3), three_point_shots_made INTEGER, three_point_attempts INTEGER, three_point_percentage DECIMAL(4,3), two_point_shots_made INTEGER, two_point_attempts INTEGER, two_point_percentage DECIMAL(4,3), free_throws_made INTEGER, free_throw_attempts INTEGER, free_throw_percentage DECIMAL(4,3), offensive_rebounds INTEGER, defensive_rebounds INTEGER, total_rebounds INTEGER, assists INTEGER, steals INTEGER, blocks INTEGER, turnovers INTEGER, personal_fouls INTEGER, points INTEGER);
